import React,{ useEffect, useState} from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useRouteMatch,
  useParams,
  useLocation,
  useHistory
} from "react-router-dom";
import queryString from 'query-string';
import axios from 'axios';
import { makeStyles } from '@material-ui/core/styles';
import {Grid,Breadcrumbs,Paper} from'@material-ui/core';



const useStyles = makeStyles((theme) => ({
  root: {
    padding:2,
    background: '#273335',

  },
  paper: {
    marginLeft:50,
    color: theme.palette.text.secondary,
    width: 350,
    height: 'auto',
    background: '#AAB4B4',
  },

  Container:{ 
    display: 'block',
    margin: 'auto',    
  },
  grid:{
    margin:10,

  },

}));
const StyledBreadcrumb = withStyles((theme: Theme) => ({
  root: {
    backgroundColor: theme.palette.grey[100],
    height: theme.spacing(3),
    color: theme.palette.grey[800],
    fontWeight: theme.typography.fontWeightRegular,
    '&:hover, &:focus': {
      backgroundColor: theme.palette.grey[300],
    },
    '&:active': {
      boxShadow: theme.shadows[1],
      backgroundColor: emphasize(theme.palette.grey[300], 0.12),
    },
  },
}))(Chip);

function App() {

  const [data, setData] = useState([]);
  // const {history} = useHistory(); 
  const {search} = useLocation();
  // const {category} = queryString.parse(search)
  const classes = useStyles();
  // const { id } = useParams();
  // const filterProductsByCategory = () =>{
  //   history.push(`/products?category=${title}`)
  // }
  // const navigateToDetails = () => history.push{`/products/${id}`}

  useEffect(() => {
    axios
      .get(`http://localhost:5000/products`)
      .then((response) => {
        setData (response.data);
        console.log(data)
      })
      .catch((error) => {
        console.log(error)
      })
  }, []);

  return (
    <div className={classes.root} >
   <header style={{textAlign:'center'}}>
        <h1>All Products</h1>
        </header>
    <Breadcrumbs aria-label="breadcrumb">
      <StyledBreadcrumb component="a" href="#" label="Catalog" onClick={handleClick} />
    </Breadcrumbs>
      {data.map((data,idx) => (
      <DataItem key={idx} {...data} />
      ))}
    </div>

  );
}

const DataItem = ({ id,name,desc,image,category,price})=>{
  const classes = useStyles();
  return (  
    <div className={classes.root}>

      <Grid container spacing={3}  >
        <Grid item xs>
          <Paper className={classes.paper}>
            <img src={image}  alt=" " style={{width:350,height:200}} />   
            <Grid item className={classes.grid}>
              <h3 >{name}</h3>  
              <p>{desc}</p>
              {category}<br></br>
              ฿{price}
            </Grid>
          </Paper>
        </Grid>

      </Grid>

      <br></br>

    </div>
  ) 

}


const Home = () =>(
  <div>
    <h1>Home</h1>
  </div>
)




const Products = () =>(
  <div>
    <h1>Products</h1>
  </div>
)

export default App;
