import React,{ useEffect, useState} from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useRouteMatch,
  useParams
} from "react-router-dom";
import axios from 'axios';
import { makeStyles } from '@material-ui/core/styles';
import {TextField,Container,Grid,ButtonBase,Typography,Paper} from'@material-ui/core';

const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      marginTop: 50,
      width: '50ch',    
    }, 
  },
  Container:{
    width: '85%',
    height: 'auto',
    display: 'block',
    margin: 'auto',    

  },
   paper: {
    padding: theme.spacing(2),
    margin: 'auto',
    maxWidth: '100%',
    background: 'SEASHELL',
  },
}));

export default function App() {

  const [data, setData] = useState([]);
  const [search,setSearch] = useState('');
  const classes = useStyles();

  useEffect(() => {
    axios
      .get(`http://localhost:5000/products`)
      .then((response) => {
        setData (response.data);
        console.log(data)
      })
      .catch((error) => {
        console.log(error)
      })
  }, []);

  return (
    <div>
   <Container className={classes.Container} >
      <h1  style={{textAlign:'center'}} >10 popular tourist attractions from wongnai</h1>
      <form style={{textAlign:'center'}} 
      >
        <TextField 
          onChange={(event) => {
            setSearch(event.target.value)
          }}
          id="outlined-full-width"
          style={{ width: 400}}
          placeholder="search"
          variant="outlined"
          name= "q"
        />
      </form>
      <br/>
      {data.map((data,idx) => (
      <DataItem key={idx} {...data} />
      ))}
    </Container>
    </div>
  );
}

const DataItem = ({ id,name,desc,image,category,price})=>{
  const classes = useStyles();
  return (

    <div className={classes.root}>
      <Paper className={classes.paper}>
        <Grid container spacing={2}>
          <Typography gutterBottom variant="subtitle1" >
                <h2 >{id}.{name}</h2>  
                <h4>{desc}</h4>
          </Typography>
          <Typography gutterBottom variant="subtitle1">
                {category}
          </Typography>
            <Grid item>
              <ButtonBase >
              {image.map((Image) => (
                 <img src={Image}  alt=" " style={{width:250,height:180}} />
               ))}
              </ButtonBase>        
            </Grid>
        </Grid>
      </Paper>
      <br></br>

    </div>
  ) 

}
