import React,{ useEffect, useState} from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useRouteMatch,
  useParams
} from "react-router-dom";
import axios from 'axios';
import { makeStyles } from '@material-ui/core/styles';
import {TextField,Container,Grid,ButtonBase,Typography,Paper} from'@material-ui/core';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    marginLeft:50,
    color: theme.palette.text.secondary,
    width: 350,
    height: 'auto',
  },

  Container:{ 
    display: 'block',
    margin: 'auto',    
  },
  grid:{
    margin:10,

  },

}));

function App() {

  const [data, setData] = useState([]);
  const [search,setSearch] = useState('');
  const classes = useStyles();

  useEffect(() => {
    axios
      .get(`http://localhost:5000/products#`)
      .then((response) => {
        setData (response.data);
        console.log(data)
      })
      .catch((error) => {
        console.log(error)
      })
  }, []);

  return (
    <div>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/category">Category</Link>
            </li> 
            <li>
              <Link to="/products">Products</Link>
            </li>
          </ul>
        </nav>
  <Switch>
        <Route  exact path="/">Home</Route>
        <Route path="/category">Categiry</Route>
        <Route path="/products">Products</Route>
        <Route path="/:id"><p>not file</p></Route>
  </Switch>

      </div>
        <header style={{textAlign:'center'}}>
        <h1>All Products</h1>
        </header>
      {data.map((data,idx) => (
      <DataItem key={idx} {...data} />
      ))}
    </div>

  );
}

const DataItem = ({ id,name,desc,image,category,price})=>{
  const classes = useStyles();
  return (  
    <div className={classes.root}>

      <Grid container spacing={3}  >
        <Grid item xs>
          <Paper className={classes.paper}>
            <img src={image}  alt=" " style={{width:350,height:200}} />   
            <Grid item className={classes.grid}>
              <h3 >{name}</h3>  
              <p>{desc}</p>
              {category}<br></br>
              ฿{price}
            </Grid>
          </Paper>
        </Grid>

      </Grid>

      <br></br>

    </div>
  ) 

}


const Home = () =>(
  <div>
    <h1>Home</h1>
  </div>
)

const Category = () =>(
  <div>
    <h1>Category</h1>
  </div>
)
const Products = () =>(
  <div>
    <h1>Products</h1>
  </div>
)

export default App;
