import React,{ useEffect, useState} from "react";
// import {
//   BrowserRouter as Router,
//   Switch,
//   Route,
//   Link,
//   useRouteMatch,
//   useParams
// } from "react-router-dom";
import axios from 'axios';
import { makeStyles } from '@material-ui/core/styles';
import {TextField,Container,Grid,ButtonBase,Typography,Paper} from'@material-ui/core';

const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      marginTop: 50,
      width: '50ch',    
    }, 
  },
  Container:{
    width: '85%',
    height: 'auto',
    display: 'block',
    margin: 'auto',    

  },
   paper: {
    padding: theme.spacing(2),
    margin: 'auto',
    maxWidth: '100%',
    background: 'SEASHELL',
  },
}));

function App() {

  const [data, setData] = useState([]);
  const [search,setSearch] = useState('');
  const classes = useStyles();

  useEffect(() => {
    axios
      .get(`http://localhost:5000/products#`)
      .then((response) => {
        setData (response.data);
        console.log(data)
      })
      .catch((error) => {
        console.log(error)
      })
  }, []);

  return (
    <div>

      {data.map((data,idx) => (
      <DataItem key={idx} {...data} />
      ))}
    </div>
  );
}

const DataItem = ({ id,name,desc,image,category,price})=>{

  return (

    <div >
      <Paper >
        <Grid container spacing={2}>
          <Typography gutterBottom variant="subtitle1" >
                <h2 >{id}.{name}</h2>  

          </Typography>
          <Typography gutterBottom variant="subtitle1">
              <h4>{desc}</h4><br></br>
                {category}<br></br>
                Pric:{price}
          </Typography>
        </Grid>
        <Typography gutterBottom variant="subtitle1">
            <Grid item>
            <img src={image}  alt=" " style={{width:250,height:180}} />     
            </Grid>
          </Typography>
      </Paper>
      <br></br>

    </div>
  ) 

}

export default App;
